import React, { useState } from 'react'

const CreateArea = (props) => {
    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const { name, value } = event.target;

        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            };
        });
    }

    function submitNote(event) {
        props.onAdd(note);
        setNote({
            title: "",
            content: ""
        });
        event.preventDefault();
    }

    return (
        <div className=''>
            <form>
                <div class="form-group">
                    <div class="form-floating mb-3">
                        <input type="text" onChange={handleChange} value={note.title} class="form-control" id="floatingInput" placeholder="title" />
                        <label for="floatingInput">title</label>
                    </div>
                    <div class="form-floating">
                        <textarea class="form-control textare" onChange={handleChange} value={note.content} id="content" rows="5" placeholder='Take a note...'/>
                    </div>
                </div>
                <button onClick={submitNote}>Add</button>
            </form>
        </div>
    )

}

export default CreateArea
