import React from "react";
import DeleteIcon from "@material-ui/icons/Delete";

const Note = (props) => {
    const today = new Date(),
        date = today.getDate() + '/' + (today.getMonth() + 1) + '/' + today.getFullYear();;

    function handleClick() {
        props.onDelete(props.id);
    }

    return (
        <div className="card bg-white border-light my-3">
            <div className="card-header fs-6 fw-bold">
                <span>{props.title}</span>
            </div>
            <div className="card-body">
                <p className="card-text fs-6">{props.content}</p>
            </div>
            <div className="card-footer p-0">
            <span className='ms-auto'><small>{date}</small></span>

                <button className="bg-white" onClick={handleClick}><DeleteIcon /></button>
            </div>
        </div>    
    );
}

export default Note;
